class EEmployees extends Component {

  constructor(props) {
  super(props)
  this.clickHandler = () => {
    //this.props.onClick();
    alert("Great Shot!");
  }

  this.data = {
    columns: [
      {
        label: 'Sr.No',
        field: 'srno',
        sort: 'asc',
        width: 150
      },
      {
        label: 'Full Name',
        field: 'name',
        sort: 'asc',
        width: 150
      },
      {
        label: 'Project Name',
        field: 'pname',
        sort: 'asc',
        width: 150
      },
      {
        label: 'Skills',
        field: 'skills',
        sort: 'asc',
        width: 100
      },
      {
        label: 'Total Logs(in Hr)',
        field: 'logs',
        sort: 'asc',
        width: 100
      },
      {
        label: 'Add Logs',
        field: 'add_logs',
        sort: 'asc',
        width: 150
      },

      {
        label: 'Update',
        field: 'edit',
        sort: 'asc',
        width: 150
      },

      {
        label: 'Delete',
        field: 'delete',
        sort: 'asc',
        width: 150
      },



    ],
    rows: [
      {
        srno:'1',
        name: 'Pragati Gaikwad',
        pname: 'Project Management',
        logs: '45',
        skills: 'HTML, CSS, React',
        view: <Button onClick={this.clickHandler}>Add</Button>,
        edit: <Button onClick={this.clickHandler}>Edit</Button>,
        delete: <Button onClick={this.clickHandler}>Delete</Button>
      },
       {
        srno:'2',
        name: 'Tapclicks Order',
        start_date: '1st Jan 2020',
        end_date: '28th Feb 2020',
        pname: 'Pragati',
        view: <Button onClick={this.clickHandler}>View</Button>,
        edit: <Button onClick={this.clickHandler}>Edit</Button>,
        delete: <Button onClick={this.clickHandler}>Delete</Button>
      },
       {
         srno:'3',
        name: 'Tiger Nixon',
        start_date: '1st Jan 2020',
        end_date: '28th Feb 2020',
        pname: 'Pragati',
        view: <Button onClick={this.clickHandler}>View</Button>,
        edit: <Button onClick={this.clickHandler}>Edit</Button>,
        delete: <Button onClick={this.clickHandler}>Delete</Button>
      },

    ]
  };

  }



  render() {
    return (
      <MDBDataTable
      striped
      hover
      data={this.data}
    />
    );
  }
}



export default EEmployees;
